    private void btn_CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_CalcularActionPerformed
        // TODO add your handling code here:
        double precioArticulo, precioFinal;
        int unidades, descuento, IVA;
        try{
            precioArticulo=Double.parseDouble(text_PrecioArt.getText());
            unidades=Integer.parseInt(text_Unidades.getText());
            descuento=Integer.parseInt(text_Descuento.getText());
            IVA=Integer.parseInt(text_IVA.getText());
            double precio_con_Descuento=(precioArticulo*unidades)-((precioArticulo*descuento)/100);
            precioFinal=precio_con_Descuento+((precio_con_Descuento*IVA)/100);
            label_PrecioFinal.setText(String.valueOf(precioFinal));
        }catch(NumberFormatException e){
            System.out.println(e.getMessage());
            e.printStackTrace();
        }catch(Exception e){
            System.out.println(e.getMessage());
            e.printStackTrace();
        }  
        
    }//GEN-LAST:event_btn_CalcularActionPerformed

